import { _ } from 'streamline-runtime';
import * as util from '../util';
import { Representation } from './representation';
import { Supervisor } from '../supervisor';

export interface ExportedPropertyData {
	CODPRO?: string;
	ALIAS?: string;
	ORDPRO?: number;
	INTPRO?: number;
	INTSHTPRO?: number;
	TYPAFFPRO?: number;
	UOMPRO?: string;
	BLCPRO?: string;
	[name: string]: any;
}

export class ExportedProperty {
	supervisor: Supervisor;
	representation: Representation;
	data: ExportedPropertyData;
	tag: string;
	constructor(superv: Supervisor, repr: Representation, data: ExportedPropertyData) {
		this.supervisor = superv;
		this.data = data;
		this.representation = repr;
	}
	init(_: _) {
		return this;
	}
};

export const Constructor = ExportedProperty;