import { LinksProto, ActionsProto } from './prototype';

export interface DiagnosisProto {
}

export interface ValueNode {
	$code: string;
	$value: string;
	$title: string;
	$locale: string;
}

export interface ReferenceNode {
	$title?: string;
	$description?: string;
	[atb: string]: any;
}

export interface DataNode {
	$uuid?: string;
	$links?: LinksProto;
	$diagnoses?: DiagnosisProto[];
	$values?: ValueNode[];
	$access?: string;
	$isDeleted?: boolean;
	$index?: number;
	$url?: string;
	[name: string]: any;
}

export interface Feed {
	$itemsPerPage?: number;
	$resources?: DataNode[];
	$links?: LinksProto;
}

export interface ResourceNode {
	$trackingId?: string;
	$baseUrl?: string;
	$prototype?: string;
	$representation?: string;
	$url?: string;
	$type?: string;
	$etag?: any;
	ETag?: any;
	$actions?: ActionsProto;
	$httpMethod?: string;
}

