"use strict";

const util = require('../supervisor/util');
import * as x3jsUtil from './util';

function isReadOnly(_, instance) {
	if (instance._parent) return isReadOnly(_, instance._parent);
	return (/^(4gl|incomplete)$/.test(instance.status(_)));
}

module.exports = {
	type: 'object',
	defineMethod: 'defineMessages',
	hasSourceFile: true,
	subdir: 'messages',
	singular: 'message',
	plural: 'messages',
	dictKey: 'LANCHP',
	entityName: 'x3jsMessage',
	metaName: 'APLSTD',
	metaFilter: {
		LAN: 'ENG'
	},
	metaSort: {
		LANCHP: 1,
		LANNUM: 1
	},
	metaTransform(_, reader, writer) {
		var result,
		    LANCHP = -Infinity;
		const flush = _ => {
			if (result && result.ENTRIES.length > 0 && !x3jsUtil.isEnum(result)) writer.write(_, result);
			result = null;
		};
		reader.forEach(_, (_, metaData) => {
			if (!metaData.LANCHP) return;
			//console.error(metaData);
			if (LANCHP !== metaData.LANCHP) {
				flush(_);
				LANCHP = metaData.LANCHP;
				result = Object.assign({}, metaData);
				result.ENTRIES = [];
			}
			if (metaData.LANNUM != null) {
				result.ENTRIES.push(metaData);
			}
		});
		flush(_);
	},
	properties: {
		module: {
			type: 'string'
		},
		name: {
			type: 'string'
		},
		dictId: {
			type: 'integer',
			dictCode: 'LANCHP'
		},
		title: {
			type: 'string',
			dictCode: 'LANMES'
		}
	},
	collections: {
		entries: {
			type: 'object',
			key: 'name',
			metaCode: 'ENTRIES',
			codeGen: 'message',
			orderCode: 'LANNUM',
			localizedKey: 'title',
			genName(meta) {
				return x3jsUtil.truncCamelize(meta.LANMES || '', 30);
			},
			properties: {
				dictId: {
					type: 'integer',
					dictCode: 'LANNUM'
				},
				title: {
					type: 'string',
					dictCode: 'LANMES'
				}
			},
			rules: {
				readOnlyProperty: isReadOnly
			}
		}
	},
	rules: {
		readOnlyProperty: isReadOnly
	}
};