"use strict";
QUnit.module(module.id);
var hb = require("html5-binary");

test('basic', 2, function() {
	var testStr = "";
	for (var i = 0; i < 2048; i++) testStr += String.fromCharCode(i % 256);
	var data = hb.stringToBinary(testStr);
	strictEqual(data.length, 2048, "data len ok");
	var str = hb.binaryToString(data);
	strictEqual(str, testStr, "roundtrip ok");
});