"use strict";

var module = QUnit.module;
var helpers = require('jsntlm/lib/helpers');

module('jsntlm');

test("Lan Manager password hash", 1, function() {

	var passw = "Beeblebrox";
	var expectedHash = "919016f64ec7b00ba235028ca50c7a03";
	var hash = helpers.hashLanManagerPassword(passw);
	console.log("Hash Lan Manager expected: "+expectedHash);
	console.log("Hash Lan Manager password: "+hash);
	strictEqual(expectedHash, hash, "Hash Lan Manager password ok");

});

test("NT password hash", 2, function() {

	var passw = "Beeblebrox";
	var expectedHash = "8c1b59e32e666dadf175745fad62c133";
	var hash = helpers.hashNtPassword(passw);
	strictEqual(expectedHash, hash, "Hash NT password ok");

	var passw = "S@ge-2013";
	var expectedHash = "d2c48ba4ae833f6bfad777ed98bb2bf5";
	var hash = helpers.hashNtPassword(passw);
	strictEqual(expectedHash, hash, "Hash NT password ok");
	
});
