    _getBaseURIParam: function(param){
        var params = document.baseURI.split("?")[1].split("&");
        var val;
        params.forEach(function(p){
            var idx = p.indexOf(param + "=");
            if (idx > -1) {
                val = p.substring(idx + param.length + 1);
                val = decodeURIComponent(val);
            }
        });
        return val;
    },
