"use strict"

var helpers = require('syracuse-core/lib/helpers');
var CommonController = require('syracuse-ui/lib/common/controller/controller').Controller;

function Controller() {
}

exports.Controller = helpers.defineClass(Controller, CommonController, {
	openPage: function(menuArticle, menu, record) {
		if(menu.$target === "wordmailmerge") {
		} else {
			CommonController.prototype.openPage.apply(this, arguments);
		}
	},
	loadRepresentation: function(article, httpQuery, success, onError, onBeforeOpen, sendRqtOpts) {
		var self = this;
		CommonController.prototype.loadRepresentation(article, httpQuery, success, onError, onBeforeOpen, sendRqtOpts);
	},
	notifyChange: function(article, binding, value) {
		var self = this;
		var notifyArgs = arguments;
		CommonController.prototype.notifyChange.apply(self, notifyArgs);
		return;
	}
});