// Returns true if this is a host that closes *before* it ends?!?!
module.exports.isAnEarlyCloseHost= function( hostName ) {
  return hostName.match(".*google(apis)?.com$")
}

//Returns true if this is a host that does not accept client ID and client secret in the header
module.exports.noAuthorizationHeaderHost= function( hostName ) {
  return hostName.match(".*google(apis)?.com$")
}

//Returns true if this is a host that does not accept URLencoded authorization token
module.exports.noURLencodingHost= function( hostName ) {
  return hostName.match(".*google(apis)?.com$")
}