var module = QUnit.module;
var sha1 = require("node-oauth/lib/sha1");

module('sha1');

test('hex sha1', 3, function() {
	equals(sha1.hex_sha1("160-bit hash"), "90D925D853C3D35CD54070BB75280FEFAD9DE9E7", "Uppercased sha1 of '160-bit hash' string");
	equals(sha1.hex_sha1("160-bit hash", true), "90D925D853C3D35CD54070BB75280FEFAD9DE9E7", "Uppercased sha1 of '160-bit hash' string");
	equals(sha1.hex_sha1("160-bit hash", false), "90d925d853c3d35cd54070bb75280fefad9de9e7", "Lowercased sha1 of '160-bit hash' string");
});