var fs = require("fs");
var combine = require("sdata-json").combine;

var proto = JSON.parse(fs.readFileSync(__dirname + "/user-proto.json", "utf8"));
var res = JSON.parse(fs.readFileSync(__dirname + "/user.json", "utf8"));
fs.writeFileSync(__dirname + "/user-full.json", JSON.stringify(combine(proto, res), null, 2), "utf8");