exports.representation = {
    $entityName: "company",
    $facets: {
        $query: {
            $layout: {
                $items: [{
                    $category: "section",
                    $layout: {
                        $items: [{
                            $bind: "$resources",
                            $format: "cardList",
                            $cardView: {
                                $layout: {
									$layoutType: "stack",
                                    $items: [
									{
                                        $bind: "code",
                                        $isTitleHidden: true
                                    }, 
									{
                                        $bind: "description",
                                        $isTitleHidden: true
                                    }
								]
                               }
                            }
                        }]
                    }
                }]
            }
        }
    }
}
