exports.representation = {
    $entityName: "employee",
    $facets: {
        $details: {
            $layout: {
                $items: [{
                    $title: "General",
                    $category: "section",
                    $layout: {
                        $items: [{
                            $title: "General",
                            $category: "block",
                            $layout: {
                                $items: [{
                                    $bind: "photo"
                                }]
                            }
                        }, {
                            $title: "Personnal informations",
                            $category: "block",
                            $layout: {
                                $items: [{
                                    $bind: "firstName"
                                }, {
                                    $bind: "lastName"
                                }, {
                                    $bind: "title"
                                }, {
                                    $bind: "maritalStatus"
                                }]
                            }
                        }, {
                            $title: "Children",
                            $category: "block",
                            $layout: {
                                $items: [{
                                    $bind: "children",
									$isTitleHidden: true
                                }]
                            }
                        }]
                    }
                }, {
                    $title: "Professional info",
                    $category: "section",
                    $layout: {
                        $items: [{
                            $title: "Address",
                            $category: "block",
                            $layout: {
                                $items: [{
                                    $bind: "addressStreet"
                                }, {
                                    $bind: "addressZipCode"
                                }, {
                                    $bind: "addressCity"
                                }, {
                                    $bind: "country"
                                }]
                            }
                        }, {
                            $title: "Misc.",
                            $category: "block",
                            $layout: {
                                $items: [{
                                    $bind: "foreignerWorker"
                                }, {
                                    $bind: "email"
                                }, {
                                    $bind: "phoneNumber"
                                }, {
                                    $bind: "company"
                                }]
                            }
                        }]
                    }
                }]
            }
        },
        $edit: {
            $copy: "$details"
        },
		$query:{
			$layout: {
            $items: [
                {
                    $category: "section",
                    $layout: {
                        $items: [
                            {
                                $bind: "$resources",
                                $format: "cardList",
                                $hidePager: true,
                                $cardView: {
                                    $layout: {
                                        $layoutType: "side",
                                        $items: [
                                            {
                                                $category: "block",
                                                $layoutType: "stack",
                                                $items: [
                                                    {
                                                        $bind: "photo",
														$isTitleHidden: true
                                                    }
                                                ]
                                            },
                                            {
                                                $layoutType: "stack",
                                                $items: [
                                                    {
                                                        $expression: "{firstName} <strong>{lastName}</strong>"
                                                    },
                                                    {
                                                        $bind: "company",
                                                        $isTitleHidden: true
                                                    }
                                                ]
                                            }
                                        ]
                                    }
                                }
                            }
                        ]
                    }
                }
            ]
        }
		}
    }
}
