"use strict";
exports.representation = {
	resources: function() {
		var locale = require("syracuse-core/lib/locale");
		var globals = require('streamline/lib/globals');
		return locale.resources(module, globals.context && globals.context.sessionLocale)();
	},
	$entityName: "group",
	$facets: {
		$details: {
			$layout: {
				$items: [{
					$title: "{@InformationsSectionTitle}",
					"$category": "section",
					$layout: {
						$items: [{
							$bind: "description"
						}, {
							$bind: "parent"
						}, {
							$bind: "children"
						}]
					}
				}, {
					$title: "{@AdministrationSectionTitle}",
					"$category": "section",
					$layout: {
						$items: [{
							$bind: "role"
						}, {
							$bind: "users"
						}, {
							$bind: "endPoints"
						}]
					}
				}]
			}
		},
		$edit: {
			$copy: "$details"
		},
		$query: {
			$layout: {
				$items: [{
					$category: "section",
					$layout: {
						$items: [{
							$bind: "$resources",
							$format: "cards",
							$cards: {
								$layout: {
									$layoutType: "stack",
									$items: [{
										$bind: "description",
										$isTitleHidden: true
									}]
								}
							}
						}]
					}
				}]
			}
		},
		$lookup: {
			$copy: "$query"
		}
	}
};