exports.representation = {
    $entityName: "role",
    $facets: {
        $details: {
            $layout: {
                $items: [{
                    $title: "{@InformationsSectionTitle}",
                    $category: "section",
                    $layout: {
                        $items: [{
                            $bind: "description"
                        }, {
                            $bind: "functionProfile"
                        }]
                    }
                }, {
                    $title: "{@AdministrationSectionTitle}",
                    $category: "section",
                    
                    $layout: {
                        $items: [{
                            $bind: "groups"
                        }, { 
                        	$bind: "badges"
                        }]
                    }
                }]
            }
        },
        $edit: {
            $copy: "$details"
        }
    }
}
