exports.representation = {
    $entityName: "team",
    $facets: {
        $details: {
            $layout: {
                $items: [{
                    $layoutType: "columns",
                    $layoutSubType: "50,50",
                    $items: [{
                        $items: [{
                            $title: "General",
                            $category: "block",
                            $layout: {
                                $items: [{
		                        	$bind: "description"
		                        },{
		                        	$bind: "isPublic"
		                        }]
                            }
                        },{
                            $title: "Administration",
                            $category: "block",
                            $layout: {
                                $items: [{
                                	$bind: "administrator"
                                },{
                                	$bind: "authors"
                                },{
                                	$bind: "members"
                                }]
                            }
                        },{
                            $title: "Content",
                            $category: "block",
                            $layout: {
                                $items: [{
                                	$bind: "documents"
                                }]
                            }
                        }]
                    },{
                        $items: [{
                            $title: "Tags",
                            $category: "block",
                            $layout: {
                                $items: [{
                                	$bind: "tags",
                                	$isTitleHidden: true
                                }]
                            }
                        }]
                    }]
                }]
            }
        },
        $edit: {
        	$copy: "$details"
        },
        $query: {
            $layout: {
                $items: [{
                    $category: "section",
                    $layout: {
                        $items: [{
                            $bind: "$resources",
                            $items: [{
                            	$bind: "description"
                            },{
                            	$bind: "isPublic"
                            }, {
                            	$bind: "administrator"
                            }]
                        }]
                    }
                }]
            }
        },
        $select: {
        	$copy: "$query"
        },
        $lookup: {
        	$copy: "$query"
        }
    }
}
