exports.representation = {
    $entityName: "user",
    $facets: {
        $details: {
            $layout: {
                $items: getPrototype(true)
            }
        },
        $edit: {
            $layout: {
                $items: getPrototype(false)
            }
        },
        $query: {
            $layout: {
                $items: [{
                    $category: "section",
                    $layout: {
                        $items: [{
                            $bind: "$resources",
                            $format: "cardList",
                            $hidePager: true,
                            $cardView: {
                                $layout: {
                                    $layoutType: "side",
                                    $items: [{
                                        $category: "block",
                                        $layoutType: "stack",
                                        $items: [{
                                            $bind: "photo",
                                            $isTitleHidden: true
                                        }]
                                    }, {
                                        $layoutType: "stack",
                                        $items: [{
                                            $bind: "fullName",
                                            $isTitleHidden: true
                                        }, {
                                            $bind: "login",
                                            $isTitleHidden: true
                                        }]
                                    }]
                                }
                            }
                        }]
                    }
                }]
            }
        }
    }
}


function getPrototype($isTitleHidden){
    return [{
        $layoutType: "columns",
        $layoutSubType: "50,50",
        $items: [{
            $items: [{
                $title: "Informations",
                $category: "block",
                $layout: {
                    $items: [{
                        $bind: "photo",
                        $isTitleHidden: $isTitleHidden
                    }, {
                        $bind: "title",
                        $isTitleHidden: $isTitleHidden
                    }, {
                        $bind: "firstName",
                        $isTitleHidden: $isTitleHidden
                    }, {
                        $bind: "lastName",
                        $isTitleHidden: $isTitleHidden
                    }, {
                        $bind: "email",
                        $isTitleHidden: $isTitleHidden
                    }]
                }
            }, {
                $title: "Login",
                $category: "block",
                $layout: {
                    $items: [{
                        $bind: "login"
                    }, {
                        $bind: "password"
                    }]
                }
            }]
        }]
    }, {
        $title: "Administration",
        $category: "block",
        $layout: {
            $items: [{
                $layoutType: "stack",
                $items: [{
                    $bind: "groups"
                }, {
                    $bind: "endpoints"
                }, {
                    $bind: "adminTeams"
                }, {
                    $bind: "authorTeams"
                }, {
                    $bind: "memberTeams"
                }]
            }]
        }
    }, {
        $title: "Personalisations",
        $category: "block",
        $layout: {
            $items: [{
                $layoutType: "columns",
                $layoutSubType: "50,50",
                $items: [{
                    $items: [{
                        $bind: "defaultRole"
                    }, {
                        $bind: "defaultLocale"
                    }]
                }]
            }]
        }
    }, {
        $title: "Custom locales",
        $category: "block",
        $layout: {
            $items: [{
                $bind: "locales",
                $isTitleHidden: true,
                $format: "cardList",
                $cardView: {
                    $layout: {
                        $layoutType: "stack",
                        $items: [{
                            $bind: "code"
                        }, {
                            $bind: "description"
                        }, {
                            $bind: "enabled"
                        }, {
                            $bind: "shortDate"
                        }, {
                            $bind: "shortTime"
                        }, {
                            $bind: "shortDatetime"
                        }, {
                            $bind: "longDate"
                        }, {
                            $bind: "longTime"
                        }, {
                            $bind: "longDatetime"
                        }, {
                            $bind: "firstDayOfWeek"
                        }, {
                            $bind: "twoDigitYearMax"
                        }, {
                            $bind: "numberDecimalSeparator"
                        }, {
                            $bind: "numberGroupSeparator"
                        }, {
                            $bind: "numberGroupSize"
                        }]
                    
                    }
                }
            }]
        }
    }] 
}

