"use strict";
var locale = require("syracuse-core/lib/locale");
var BigInteger = require("../../deps/bigInteger").BigInteger;
//var BigInteger=require("./bigInteger").BigInteger;
exports.validate = function(value, constraints, errors) {
	if (value == null) {
		if (constraints.$isMandatory) return errors.push(locale.format(module, "valMan"));
		if (!constraints.$isNullable) return errors.push(locale.format(module, "valNull"));
		return;
	}
	if (!(value instanceof BigInteger)) {
		errors.push(locale.format(module, "valBig"));
		return;
	}
};

function _make(value) {
	return new BigInteger("0" + value);
}

BigInteger.prototype.valueOf = function() {
	return this.toString();
};
exports.make = _make;