"use strict";
var BigInteger=require("../../deps/bigInteger").BigInteger;
//var BigInteger=require("./bigInteger").BigInteger;
exports.validate = function(value, constraints, errors){
	if (value == null) {
		if (constraints.$isMandatory) 
			return errors.push("value is mandatory");
		if (!constraints.$isNullable) 
			return errors.push("value cannot be null");
		return;
	}
	if (!(value instanceof BigInteger)) {
		errors.push("value must be a bigint");
		return;
	}
};
function _make(value){
	return new BigInteger("0"+value);
}

BigInteger.prototype.valueOf=function() { return this.toString(); };
exports.make = _make;
