"use strict";
var phoneFormat = require('../../lib/phoneFormat');

import { assert } from 'chai';
Object.keys(assert).forEach(key => {
	if (key !== 'isNaN') global[key] = assert[key];
});

describe(module.id, () => {

	it('Find country by phone', function() {
		strictEqual(phoneFormat.countryForE164Number("+33612244896"), "FR");
		strictEqual(phoneFormat.countryForE164Number("+12025550104"), "US");
		strictEqual(phoneFormat.countryForE164Number("+61253648121"), "AU");
		strictEqual(phoneFormat.countryForE164Number("+43253648"), "AT");
	});
});