"use strict";

exports.contract = {
	application: "syracuse",
	contract: "etl",
	entities: {
		device: require('./entities/device').entity,
		mongoDatabase: require('./entities/mongoDatabase').entity,
		mailTemplate: require('./entities/mailTemplate').entity,
		pipe: require('./entities/pipe').entity,
		//stream: require('./entities/stream').entity,
	},
	representations: {},
};

exports.extendContract = function(contract) {
	function copy(src, dst) {
		Object.keys(src).forEach(function(name) {
			dst[name] = src[name];
		});
	}
	copy(exports.contract.entities, contract.entities);
	copy(exports.contract.representations, contract.representations);
};