exports.sessionManager = {
	sessionManagement : function(callback, request, response) {
		callback(null, {
			session : _session
		});
	}
};

var _session = exports.sessionManager._session = {
	_data : {},
	setData : function(name, value) {
		_session._data[name] = value;
	},
	getData : function(name) {
		return _session._data[name];
	},
	_reset : function() {
		_session._data = {};
	}
};