function Socket() {
	this.connected = false;
	this.connecting = false;
	this._messagesSent = [];
	this._events = {};
}
Socket.prototype._emit = function(event, values) {
	if (this._events[event]) {
		this._events[event].forEach(function(callback) {
			callback(values);
		});
	}
};
Socket.prototype._reset = function(all) {
	if (all) {
		this.connected = false;
		this.connecting = true;
		this._events = {};
	}
	this._messagesSent = [];
};
Socket.prototype.connect = function() {
	this.connected = true;
	this.connecting = false;
	this._emit("connect");
};
Socket.prototype.disconnect = function() {
	this.connected = false;
	this.connecting = false;
};
Socket.prototype.on = function(event, callback) {
	this._events[event] = this._events[event] || [];
	this._events[event].push(callback);
};
Socket.prototype.send = function(messages) {
	this._messagesSent.push(messages);
};

window.io = exports.io = {
	Socket : Socket
};