"use strict";
var balancer = require('syracuse-load/lib/balancer');
var util = require('util');
var fs = require('fs');

QUnit.module(module.id);

test('hash', 4, function() {
	strictEqual(balancer._h(""), 0, "Hash of ''");
	strictEqual(balancer._h("a"), 97, "Hash of 'a'");
	strictEqual(balancer._h("ab"), 3105, "Hash of 'ab'");
	strictEqual(balancer._h("abc"), 96354, "Hash of 'abc'");
});


test('Compare connecions', 9, function() {
	strictEqual(balancer._cc({
		port: 1
	}, {
		port: 2
	}), false, "Different ports");
	strictEqual(balancer._cc({
		active: true,
		port: 1
	}, {
		active: false,
		port: 1
	}), false, "Active");
	strictEqual(balancer._cc({
		port: 1,
		ssl: false
	}, {
		port: 1,
		ssl: true
	}), false, "SSL1");
	strictEqual(balancer._cc({
		port: 1,
		ssl: true
	}, {
		port: 1,
		ssl: false
	}), false, "SSL2");
	strictEqual(balancer._cc({
		port: 1,
		ssl: true,
		serverCert: {
			name: "s1"
		}
	}, {
		port: 1,
		ssl: true,
		serverCert: {
			name: "s2"
		}
	}), false, "different server certificate");
	strictEqual(balancer._cc({
		port: 1,
		ssl: true,
		serverCert: {
			name: "s1"
		},
		clientAuth: true
	}, {
		port: 1,
		ssl: true,
		serverCert: {
			name: "s1"
		}
	}), false, "different client auth");
	strictEqual(balancer._cc({
		port: 1,
		ssl: true,
		serverCert: {
			name: "s1"
		},
		clientAuth: true,
		clientCert: {
			name: "c1"
		}
	}, {
		port: 1,
		ssl: true,
		serverCert: {
			name: "s1"
		},
		clientAuth: true,
		clientCert: {
			name: "c2"
		}
	}), false, "different client cert");
	strictEqual(balancer._cc({
		port: 1,
		ssl: false,
		serverCert: {
			name: "s1"
		},
		clientAuth: true,
		clientCert: {
			name: "c1"
		}
	}, {
		port: 1,
		ssl: false,
		serverCert: {
			name: "s2"
		}
	}), true, "differences in unused SSL connection data");
	strictEqual(balancer._cc({
		port: 1,
		ssl: false,
		serverCert: {
			name: "s1"
		},
		clientAuth: false,
		clientCert: {
			name: "c1"
		}
	}, {
		port: 1,
		ssl: false,
		serverCert: {
			name: "s2"
		},
		clientAuth: false
	}), true, "differences in unused SSL connection data");
});

test('Balancer', 4, function() {
	var host1 = {
		hostname: "a",
		status: 3,
		children: 2
	};
	var host2 = {
		hostname: "b",
		status: 3,
		children: 3
	};
	var res = balancer.balance({
		"a:N0": 1,
		"a:N1": 2
	}, [host1], "a", undefined, 0);
	strictEqual(JSON.stringify(res), JSON.stringify([host1, "N0"]), "balance 1 port N0");
	var res = balancer.balance({
		"a:N0": 2,
		"a:N1": 2
	}, [host1], "a", undefined, 0);
	strictEqual(JSON.stringify(res), JSON.stringify([host1, "N0"]), "balance 1 port N0");
	var res = balancer.balance({
		"a:N1": 2,
		"a:N0": 3
	}, [host1], "a", undefined, 0);
	strictEqual(JSON.stringify(res), JSON.stringify([host1, "N1"]), "balance 1 port N1");
	var res = balancer.balance({
		"a:N1": 1,
		"a:N0": 2,
		"b:N0": 1
	}, [host1, host2], "a", undefined, 0);
	strictEqual(JSON.stringify(res), JSON.stringify([host2, "N2"]), "balance 2 port N2");

});

test('Host update', 14, function() {
	var hosts = [{
		hostname: "A1",
		started: false,
		deactivated: false,
		connectionData: [{
			active: true
		}]
	}, {
		hostname: "A2",
		started: true,
		connectionData: [{
			active: true
		}]
	}];
	var oldHosts = [{
		hostname: "A1",
		started: true,
		connectionData: [{
			active: true
		}]
	}, {
		hostname: "A2",
		started: false,
		connectionData: [{
			active: true
		}]
	}];
	var res = balancer._updateHosts(oldHosts, hosts, "A1");
	strictEqual(res[0].hostname, "A1", "local host name");
	strictEqual(res[0].started, false, "local host started");
	strictEqual(res[1].hostname, "A2", "other host name");
	strictEqual(res[1].started, true, "other host started");
	strictEqual(res[0].status, 1, "status local host (init)");
	strictEqual(res[1].status, -5, "status other host (unknown)");
	strictEqual(res.length, 2, "number of hosts");

	var hosts = [{
		hostname: "A1",
		deactivated: false,
		started: true,
		connectionData: [{
			active: true
		}]
	}, {
		hostname: "A2",
		deactivated: false,
		started: true,
		connectionData: [{
			active: true
		}]
	}];
	var oldHosts = [{
		hostname: "A1",
		started: false,
		connectionData: [{
			active: true
		}]
	}, {
		hostname: "A2",
		started: false,
		connectionData: [{
			active: true
		}]
	}];
	var res = balancer._updateHosts(oldHosts, hosts, "A1");
	strictEqual(res[0].hostname, "A1", "local host name");
	strictEqual(res[1].status, -5, "other host unknown status");
	var hosts = [{
		hostname: "A1",
		started: false,
		connectionData: [{
			active: true,
			port: 8112
		}]
	}, {
		hostname: "A2",
		started: true,
		connectionData: [{
			active: true
		}]
	}];
	var oldHosts = [{
		hostname: "A1",
		started: true,
		connectionData: [{
			active: true,
			port: 8111
		}]
	}];
	var res = balancer._updateHosts(oldHosts, hosts, "A1");
	strictEqual(res[0].hostname, "A1", "first host name");
	strictEqual(res[0].stop, true, "stop host");
	strictEqual(res[1].hostname, "A2", "other host name");
	strictEqual(res[1].started, true, "other host started");
	strictEqual(res.length, 2, "number of hosts");
});