"use strict";
var siteFactory = require('syracuse-ui/lib/site/site');
exports.main = function() {
	var landscape = window.orientation && (window.orientation == 90 || window.orientation == -90);
	siteFactory.load({
		$device: "tablet",
		$preferences: {
			convergenceBar: {
				$viewMode: "float",
				$isCollapsed: true
			},
			menuBar: {
				$viewMode: "dock",
				$isCollapsed: landscape ? false : true
			},
			"dash-menubar": {
				$viewMode: "dock",
				$isCollapsed: false
			}
		}
	});
};
if (require.main == module)
	exports.main();