var lib = require("./manifest.js");
 
lib.mkmanifest({
	filename : "mobile"
	,path : "../../../"
	,dst : "../../../../"
	,prefix : "./mobile"
	,version : "v1.0"
	,content : "/favicon.ico\n./moffline.html\n"
	,exclude : ['.*/[.].*', '.*/dev/.*','.*/build/.*', '.*/auth/.*', '.*/debug/.*', '\.cache.manifest','.*/flot/.*']
	,network : ['mobile.appcache\nmobile.html\n*']
	,fallback : ['./mobile.html ./moffline.html']
});