'use strict';
(function($) {
	var that = $.smobile;
	that.controls = that.controls || {};
	
	that.controls.Lookup={
		createLookup: function(controller, c) {
			var options= c.data || {};
			var html = ['<div data-role="fieldcontain" id="'+c.id+'">'];
			html.push('<div class="s-m-state-block s-m-error" data-role="none" id="'+c.id+'_error"></div>');
			html.push('<label class="ui-input-text" for="'+c.id+'_i"><a>'+$.helpers.htmlEncode(options.label)+(options.$isMandatory?"*":"")+'</a></label>');
			html.push('<input ');
			html.push('type="text" name="'+c.id+'" id="'+c.id+'_i" ');
			html.push('value="');
			var cv = controller.getValue(options.bind, null, 0);	
			html.push($.helpers.htmlEncode(cv.$value || ""));
			html.push('"');
			html.push('/>');
			html.push('</div>');
			options.$lookup = 1;
			return { 
				html: html.join(''),
				bind: options.bind,
				action: options.action,
				options: options
			};
		},	

		handler: function(c, after, layoutClass) {
			return after(that.controls.createLookup.createLookup(this, c));
		},
		events: function($c, c) {
			var controller = this;
			var $input = $c.find( '#'+ c.id + "_i");
			$input.blur(function() {
				var _aftercchange = controller.afterChangeHnd(c), value;
					
				if (c.bind) {
					controller.checkChange(c.id, $input.val(), _aftercchange);
				}
			});
		}
	};
	that.controls.Lookup.checkValue = that.controls.Edit.checkValue;
	
})(jQuery);