	that.applySDataSort = function(sort, res) {
		var sh = function(a, b) {
			var v1, v2;
			for (var i= 0, len= sort.length; i<len; i++) {
				var so= sort[i];
				v1 = a[so.name];
				v2 = b[so.name];
				if (v1 == v2) continue;
				if (v1 > v2) {
				
					return (so.asc?1:-1);
				} else {
					return (so.asc?-1:1);
				}	
			}
			return 0
		};
		res.sort(sh);
	};	
