		_getSData:  function(db, purl, cb) {
			if (!_checkDBHandler(db, cb)) return;
			try {	
				var gmap=[], gvalues = {};
				var su = $.sdata.url2local(purl);
				if (purl.id ||  purl.template) {
					db.readTransaction(function(tx) {
						tx.executeSql('SELECT * FROM sdata where url=? and status<?', [su, $.sdata.STATUS.DELETED],function (tx, results) {
							var rows = results.rows;
							if (rows.length) 
								cb(_addInfo(rows.item(0), JSON.parse(rows.item(0).data)), {status: 200});		
							else
								cb(null, {status: 404});
						});
					}, function() {
						cb(null, {status: 404});
					});
				} else if (purl.entity) {
					var result = {
						$url: purl.uri,
						"$resources": []
					}
					db.readTransaction(function(tx) {
						var smap = $.sdata.url2search(purl);
						tx.executeSql('SELECT * FROM sdata where entity_url=? and  appid=? and status<?', [su, $.sdata.config.id, $.sdata.STATUS.DELETED],function (tx, results) {
							var rows = results.rows;
							if (rows.length)  {
								for (var jj =0, ll= rows.length; jj<ll; jj++) {
									var row = rows.item(jj);
									_addResource(row, JSON.parse(row.data), result.$resources, purl, gmap, gvalues, smap);
								}
								if (purl.pgroupBy) 
									for (var ii in gvalues) result.$resources.push(gvalues[ii]);	
								_applySort(purl,result.$resources); 
								cb(result, {status: 200});
								
							} else 	
								cb(result, {status: 200});
								
						});
					}, function() {
						cb(result, {status: 404});
					});
				}
			} catch(ex) {
				console.log(ex);
			}
		},
