				$.helpers.each(["draft", "sdata", "apps"], function(table, index, next) {
					tx.executeSql('drop table if exists '+ table, [], function (tx, result) {
					next();
					}, function(tx, error) {
						console.log(error);	
						next();
					});
				}, function() {
					options.success(db);
				});
