(function($) {
	var that = $.smobile || {};
	var ui = {};
	that.staticui = ui;
	ui.start = function() {	
		var locale = $.smobile.locale();
		return {
			"$title": locale.administration.title,
			"content": {
				"title": {"uiClass":"Html", "data" : { "html": "<div><center><h4>"+locale.administration.welcome+"</h4></center></div>" }},
				"menu": { "uiClass":"MainMenu", "data" :{"items": []}} 
			}
		};
	};
	ui.about = function() {	
		var locale = $.smobile.locale();
		return {
			"$title": locale.administration.title,
			"content": { "title": {"uiClass":"Html", "data" : { "html": locale.about }}}
		};
	};
	ui.mainfooter = function() {	
		var locale = $.smobile.locale();
		return  {"footer": {
			"uiClass":"NavBar", 
			"data":{ 
				"items": [
					{ "type": "mainmenu"},
					{ "type": "setup"},
					{ "type": "help"}
				]
			}
		}};
	
	};
	ui.appFooter = function() {	
		return  {"footer": {
			"uiClass":"NavBar", 
			"data":{ 
				"items": [
					{ "type": "sync"},
					{ "type": "home"}
				]
			}
		}};
	
	};
	
	ui.applications = function() {	
		return  {"content": { "apps" : {"uiClass":"AppsView"}}};
	};
	ui.errors = function() {	
		var locale = $.smobile.locale();
		return {
			"content": {
				"title" : {"uiClass":"Html", "data" : { "html": "<h1>"+locale.errorpagetitle+"</h1>" }},
				"errors" : {"uiClass":"ErrorView"}
			}
		};
	};
	ui.send = function() {	
		var locale = $.smobile.locale();
		return {
			"$title": locale.sendpagetitle,
			"content": {
				"toSend": {"uiClass":"SyncView"},
				"doSync": {"uiClass":"SyncCheck"},
				"log": {"uiClass":"ErrorView"}
			},
			"footer": { "footer" : {"uiClass":"SyncFooter" }}
		};
	};
	
	
})(jQuery);    
