"use strict"; 
(function($) {
	var that = $.smobile || {};
	var $helpers = $.helpers;
	that.staticui = that.staticui || {};
	var ui = that.staticui;
	ui.start = function() {	
		var locale = $helpers.locale();
		return {
			"$title": locale.administration.title,
			"content": {
				"title": {"uiClass":"Html", "data" : { "html": "<div><center><h4>"+locale.administration.welcome+"</h4></center></div>" }},
				"menu": { "uiClass":"MainMenu", "data" :{"items": []}} 
			}
		};
	};
	ui.about = function() {	
		var locale = $helpers.locale();
		return {
			"$title": locale.administration.title,
			"content": { 
				"cp": {"layout":"BlockBodyLayout", "data" : {}},
				"title": {"uiClass":"Html", "data" : { "html": locale.about }, "parent": "cp" }
			}
		};
	};
	ui.settings = function() {	
		var locale = $helpers.locale();
		return {
			"$title": locale.administration.title,
			"content": { 
				"cp": {"layout":"BlockBodyLayout", "data" : {}},
				"errors" : {"uiClass":"ErrorView", "parent": "cp"}
			}
		};
	};
	
	
	ui.mainfooter = function() {	
		var locale = $helpers.locale();
		return  {"footer": {
			"uiClass":"NavBar", 
			"data":{ 
				"items": [
					{ "type": "mainmenu"},
					{ "type": "setup"},
					{ "type": "help"},
					{ "type": "settings"}
				]
			}
		}};
	
	};
	ui.appFooter = function() {	
		return  {"footer": {
			"uiClass":"NavBar", 
			"data":{ 
				"items": [
					{ "type": "sync"},
					{ "type": "home"}
				]
			}
		}};
	
	};
	
	ui.applications = function() {	
		return  {"content": { 
			"apps" : {"uiClass":"AppsView"}, 
			"cp": {"layout":"BlockBodyLayout", "data" : {}},
			"errors" : {"uiClass":"ErrorView", "parent": "cp"}}
		};
	};
	ui.apperrors = ui.errors = function() {	
		var locale = $helpers.locale();
		return {
			"content": {
				"cp": {"layout":"BlockBodyLayout", "data" : {}},
				"title" : {"uiClass":"Html", "data" : { "html": "<center><h2>"+locale.errorpagetitle+"</h2></center>" }, "parent": "cp"},
				"errors" : {"uiClass":"ErrorView", "parent": "cp"}
			}
		};
	};
	ui.send = function() {	
		var locale = $helpers.locale();
		return {
			"$title": locale.sendpagetitle,
			"content": {
				"cp": {"layout":"BlockBodyLayout", "data" : {}},
				"toSend": {"uiClass":"SyncView", "parent": "cp" },
				"doSync": {"uiClass":"SyncCheck", "parent": "cp" },
				"log": {"uiClass":"ErrorView", "parent": "cp" }
			},
			"footer": { "footer" : {"uiClass":"SyncFooter" }}
		};
	};
	
	
})(jQuery);    


