"use strict";
(function($) {
	var that = $.smobile;
	that.controls = that.controls || {};
	that.controls.MainMenu = {
		sync: true,
		handler: function(c, after) {
			var controller = this;
			var html = [],
				options = c.data || {};
			var items = options.items || [];
			items.forEach(function(v) {
				v.data = v.data || {};
				v.data.menu = true;
			});
			return that.controls.List.handler.apply(controller, [c, after]);
		}
	};
})(jQuery);