	that.each = function(array, fn, next, parallel) {
		array = array || [];
		if (!array.length) return next();
		if (next == null) {
			return array.forEach(fn);
		}
		parallel = parallel || 1;
		var pending = 0;
		var i = 0,
			len = array.length;
		(function advance() {
			if (i == len && !pending) return next();
			while (i < len && pending < parallel) {
				pending++;
				var j = i++;
				fn(array[j], j, function(stop) {
					if (stop === true) {
						pending = 0;
					} else {
						pending--;
					}
					advance();
				});
			}
		})();
	};

