"use strict";
(function($) {
	var that = $.smobile || {};
	var $helpers = $.helpers;
	that.staticui = that.staticui || {};
	var ui = that.staticui;
	ui.start = function() {
		var locale = $helpers.locale();
		return {
			"$title": locale.administration.title,
			"content": {
				"title": {
					"uiClass": "Html",
					"data": {
						"html": "<div><center><h4>" + locale.administration.welcome + "</h4></center></div>"
					}
				},
				"menu": {
					"uiClass": "AppDashboard",
					"data": {
						"items": []
					}
				}
			}
		};
	};
	ui.logout = function() {
		var locale = $helpers.locale();
		var loc = window.location.href;
		if (loc.indexOf('#') >= 0) loc = loc.substring(0, loc.indexOf('#'));
		var loc = loc + "?forcereload=" + new Date().getTime();
		return {
			"$title": locale.administration.title,
			"content": {
				"title": {
					"uiClass": "Html",
					"data": {
						"html": "<div><center><h4>" + $helpers.locale().logoutmsg + "</h4><br> <a href='" + loc + "'>Home</a></center></div>"
					}
				}
			}
		};
	};


	ui.about = function() {
		var locale = $helpers.locale();
		return {
			"$title": locale.administration.title,
			"content": {
				"cp": {
					"layout": "BlockBodyLayout",
					"data": {}
				},
				"title": {
					"uiClass": "Html",
					"data": {
						"html": locale.about + '<br /> <p>' + locale.ui.buildDate + $helpers.date.toString($helpers.buildDate()) + '</p>'
					},
					"parent": "cp"
				}
			}
		};
	};
	ui.settings = function() {
		var locale = $helpers.locale();
		return {
			"$title": locale.administration.title,
			"content": {
				"cp": {
					"layout": "BlockBodyLayout",
					"data": {}
				},
				"errors": {
					"uiClass": "ErrorView",
					"parent": "cp"
				}
			}
		};
	};
	ui.apppanel = function(addback) {
		var r = {
			"uiClass": "panelView",
			"name": "apppanel",
			"data": {
				items: [{
					title: "",
					"items": [{
						"type": "mainmenu"
					}]
				}]
			}
		};
		if (addback === true) {
			r.data.items[0].items.push({
				"type": "back"
			});
		}
		return r;
	};
	ui.ctxpanel = function() {
		var locale = $helpers.locale();
		return {
			"uiClass": "panelView",
			"name": "ctxpanel",
			"data": {
				items: [{
					title: "",
					"items": []
				}]
			}
		};
	};

	ui.mainpanel = function() {
		var locale = $helpers.locale();
		return {
			"uiClass": "panelView",
			"name": "mainpanel",
			"data": {
				title: "",
				items: [{
					"items": [{
						"type": "mainmenu"
					}, {
						"type": "setup"
					}, {
						"type": "help"
					}, {
						"type": "settings"
					}, {
						"type": "logout"
					}, {
						"type": "testerrors"
					}]
				}]
			}
		};

	};
	ui.applications = function() {
		return {
			"content": {
				"cp": {
					"layout": "BlockBodyLayout",
					"data": {}
				},
				"errors": {
					"uiClass": "ErrorView",
					"parent": "cp"
				},
				"apps": {
					"uiClass": "AppsView"
				}
			}
		};
	};
	ui.apperrors = ui.errors = function() {
		var locale = $helpers.locale();
		return {
			"content": {
				"cp": {
					"layout": "BlockBodyLayout",
					"data": {}
				},
				"title": {
					"uiClass": "Html",
					"data": {
						"html": "<center><h2>" + locale.errorpagetitle + "</h2></center>"
					},
					"parent": "cp"
				},
				"errors": {
					"uiClass": "ErrorView",
					"parent": "cp"
				}
			}
		};
	};
	ui.send = function() {
		var locale = $helpers.locale();
		return {
			"$title": locale.sendpagetitle,
			"content": {
				"cp": {
					"layout": "BlockBodyLayout",
					"data": {}
				},
				"log": {
					"uiClass": "ErrorView",
					"parent": "cp"
				},
				"toSend": {
					"uiClass": "SyncView",
					"parent": "cp"
				}
			}
		};
	};

})(jQuery);