"use strict";
var patch = require('../../lib/integrate');
var unit = require('../fixtures/unit');
var fs = require('streamline-fs');
import { assert } from 'chai';
Object.keys(assert).forEach(key => {
	if (key !== 'isNaN') global[key] = assert[key];
});

describe(module.id, () => {

	it('start', function(_) {
		fs.mkdir("patchunittest", _);
	});

	it('create', function(_) {
		patch.normalPatch("patchunittest", __dirname + "/../fixtures/umlaut_create.patch", {}, _);
		var errors = unit.testhash("patchunittest", {
			'/umlaut.txt': 'b512cd7434daac9a864cb7a60a9be4c1fb82e39c'
		}, _);
		strictEqual(errors, "");
	});

	it('change', function(_) {
		patch.normalPatch("patchunittest", __dirname + "/../fixtures/umlaut_change.patch", {}, _);
		var errors = unit.testhash("patchunittest", {
			'/umlaut.txt': '2d408a57a209de051b7de50975ae1a9d9ff6654b'
		}, _);
		strictEqual(errors, "");
	});

	it('delete', function(_) {
		patch.normalPatch("patchunittest", __dirname + "/../fixtures/umlaut_delete.patch", {}, _);
		var errors = unit.testhash("patchunittest", {}, _);
		strictEqual(errors, "");
	});

	it('end', function(_) {
		unit.rmdirRec("patchunittest", _);
	});
});