var parser = require('./rtfparse');
var interpret = require('./rtfinterpret');
var build = require('./buildhtml');

/// !doc
/// # RTF to HTML converter
/// 
/// `var convert = require('rtf_html_converter');`  
/// `html = convert(rtf, options);`  
module.exports = function(rtf, options) {
	// will see about options later
	var root = parser.parse(rtf);
	var model = interpret.interpret(root);
	return build.buildHtml(root, model);
}