"use strict";
var operators = require('./operator');

exports.tokenType = {
	identifier: 'identifier',
	operator: 'operator',
	literal: 'literal'
};

exports.Token = class Token {
	constructor(type, line, offset, length, val) {
		var self = this;
		self.type = type;
		self.value = val;

		var _line = line;
		var _offset = offset;
		var _length = length;
		self.getRemainingText = function() {
			return _line.substring(_offset, _line.length);
		};
	}
	matches(code) {
		return this.value instanceof operators.Operator && this.value.code == code;
	}
};