"use strict"

exports.representation = {
	$entityName: "queryResult",
	$facets: {
		$query: {
            $layout: {
                $items: [{
                    $category: "section",
                    $layout: {
                        $items: [{
                            $bind: "$resources",
                            $format: "cards",
                            $cards: {
                                $layout: {
                                    $items: [{
                                        $bind: "type"
                                    }, {
                                        $bind: "description"
                                    }]
                                }
                            }
                        }]
                    }
                }]
            }
		},
		$search: {
			$copy: "$query"
		}
	}
}