"use strict";

var config = require('syracuse-main/lib/nodeconfig').config;
var helpers = require("syracuse-core/lib/helpers");
var tracer = config.soap && config.soap.request.tracer != null ? config.soap.request.tracer : false;


var SoapTracer = helpers.defineClass(function() {

}, null, {
	traceSection: function(title, content){
		this.trace("\n");
		this.trace("##################################################");
		this.trace("#    "+title);
		this.trace("##################################################");
		if (content != null && content !== "")
			this.trace(content);
	},
	traceSubSection: function(title, content){
		this.trace("\n");
		this.trace("--------------------------------------------------");
		this.trace("|    "+title);
		this.trace("--------------------------------------------------");
		
		if (content != null && content !== "")
			this.trace(content);
	},
	trace: function(str){
		if (tracer === true)
			console.log(" "+str);
	}
});

exports.create = function(){
	return new SoapTracer();
}