"use strict";

var config = require('syracuse-main/lib/nodeconfig').config;
var helpers = require("syracuse-core/lib/helpers");



var SoapTracer = helpers.defineClass(function(name) {
	this.tracer = config.soap && config.soap[name].tracer != null ? config.soap[name].tracer : false;
}, null, {
	traceSection: function(title, content){
		this.trace("\n");
		this.trace("##################################################");
		this.trace("#    "+title);
		this.trace("##################################################");
		if (content != null && content !== "")
			this.trace(content);
	},
	traceSubSection: function(title, content){
		this.trace("\n");
		this.trace("--------------------------------------------------");
		this.trace("|    "+title);
		this.trace("--------------------------------------------------");
		
		if (content != null && content !== "")
			this.trace(content);
	},
	trace: function(str){
		if (this.tracer === true)
			console.log(" "+str);
	}
});

exports.create = function(name){
	return new SoapTracer(name);
}