"use strict";

exports.get = function(entity){
	
	
	var title = entity.charAt(0).toUpperCase() + entity.slice(1);
	
	var operations = {}
	// READ OPERATION
	var $read = {
		$title: "read"+title,
		$request: {
			$type: "application/x-reference",
			$: {
				$key: {
					$isMandatory: true,
					$type: "xs:string"
				},
				$binary: {
					$type: "xs:boolean",
					$default: "false"
				}
			}
		},
		$response: {
			$type: "application/x-reference",
			$: {}
		}
	}
	$read.$response.$[entity] = {
		$type: "tns:" + entity
	}

	
	var operations = {}
	// CREATE OPERATION
	var $create = {
		$title: "create"+title,
		$request: {
			$type: "application/x-reference",
			$: {}
		},
		$response: {
			$type: "application/x-reference",
			$: {
				$key: {
					$type: "application/x-string",
					$isMandatory: true
				}
			}
		}
	}
	$create.$request.$[entity] = {
		$type: "tns:" + entity,
		$isMandatory: true
	}
	
	// DELETE OPERATION
	var $delete = {
		$title: "delete"+title,
		$request: {
			$type: "application/x-reference",
			$: {
				$key: {
					$isMandatory: true,
					$type: "xs:string"
				}
			}
		},
		$response: {
			$type: "application/x-reference",
			$: {
				deleted: {
					$type: "xs:boolean"
				}
			}
		}
	}

	
	// LIST OPERATION
	var $list = {
		$title: "list"+title,
		$request: {
			$type: "application/x-reference",
			$: {
				$binary: {
					$type: "xs:boolean",
					$default: "false"
				}
			}
		},
		$response: {
			$type: "application/x-reference",
			$: {}
		}
	}
	$list.$response.$["ArrayOf" + title] = {
		$type: "application/x-collection",
		$item: {
			$type: "application/x-reference",
			$: {}
	    }
	}
	$list.$response.$["ArrayOf" + title].$item.$[entity] = {
		$type: "tns:" + entity
	}
	
	
	
	return {
		$create: $create,
		$read: $read,
		$delete: $delete,
		$list: $list
	}

	
}