	if (repr.length === 5) {
		repr = repr.slice(3, 5);
	}

	if (repr[0].smStartsWith("QUERY~") || repr[0].smStartsWith("STATS~")) {
		var p = repr[0].split("~");
		repr[0] = p[1];
	}

