"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var globals = require('syracuse-tablet/html/js/helpers/globals');
var locale = require('syracuse-tablet/html/js/helpers/locale');

exports.enableAuthCss = function() {
	$(".s-m-control").addClass("s-m-auth");
	$(".s-m-control.s-m-auth").on("click", _OnSelectControl);
};

exports.disableAuthCss = function() {
	$(".s-m-control.s-m-auth").off("click", _OnSelectControl);
	$(".s-m-control").removeClass("s-m-auth");
};

function _OnSelectControl() {
	$(".s-m-control.s-m-auth").removeClass("s-m-auth-selected");
	$(this).addClass("s-m-auth-selected");
	return false;
}