"use strict";

var _panelWidth = 300;

/*
 * Calculate layout of authoring panel
 */
exports.updateLayout = function(panel, panelWidth) {
	var width = $(window).width();
	var height = $(window).height();

	if (panelWidth != null) {
		_panelWidth = panelWidth;
	}

	if (panel.$$authPanelRight.is(":visible")) {
		panelWidth = panel.$$authPanelRight.width();
		panel.$$authPanelRight.width(_panelWidth);
		panel.$$authPanelRight.css("left", width - _panelWidth + "px");
		panel.$$authPanelRight.css("height", height + "px");

		$("#s-m-app-id").css("width", width - _panelWidth + "px");
		$("#s-m-app-id").css("height", height + "px");

		panel.$$authPanelRight.find(".tab-content").css("height", height - 100 + "px");
	} else {
		$("#s-m-app-id").css("width", width + "px");
	}
};