"use strict";

var locale = require('syracuse-tablet/html/js/helpers/locale');

var _properties = {
	"$isTitleHidden": {
		"key": "$isTitleHidden",
		"default": false,
		"values": [{
			"value": true
		}, {
			"value": false
		}]
	},
	"$labelPosition": {
		"key": "$labelPosition",
		"default": "left",
		"values": [{
			"value": "left"
		}, {
			"value": "top"
		}, {
			"value": "bottom"
		}, {
			"value": "right"
		}]
	},
	"$labelAlign": {
		"key": "$labelAlign",
		"default": "left",
		"values": [{
			"value": "left"
		}, {
			"value": "right"
		}]
	},
	"$theme": {
		"key": "$theme",
		"default": "a",
		"values": [{
			"value": "a"
		}, {
			"value": "b"
		}, {
			"value": "c"
		}]
	},
	"$tileColor": {
		"key": "$tileColor",
		"default": "lime",
		"values": [{
			"value": "purple"
		}, {
			"value": "magenta"
		}, {
			"value": "teal"
		}, {
			"value": "brown"
		}, {
			"value": "pink"
		}, {
			"value": "orange"
		}, {
			"value": "blue"
		}, {
			"value": "red"
		}, {
			"value": "green"
		}]
	},
	"$tileSize": {
		"key": "$size",
		"default": "medium",
		"values": [{
			"value": "small"
		}, {
			"value": "medium"
		}, {
			"value": "wide"
		}, {
			"value": "large"
		}, {
			"value": "full"
		}]
	},
};

// Controls and layout classes that cannot be selected during authoring
var _elementsNoAuthoring = [".s-m-vignette", ".s-m-hub"];
exports.elementsNoAuthoring = _elementsNoAuthoring;

var _initProperties;
/* TODO: Handle language change */
exports.initProperties = function() {
	if (_initProperties) {
		return;
	}

	var pre = "auth";
	Object.keys(_properties).forEach(function(prop) {
		var lk = pre + "." + prop;
		var p = _properties[prop];
		p.label = locale.text(lk);
		if (p.values) {
			p.values.forEach(function(v) {
				var vk = lk + "." + ("" + v.value);
				v.label = locale.text(vk);
			});
		}
	});
	_initProperties = true;
};

var _layoutProps = {
	"stack": [],
	"hub-group": [],
	"tile": [_properties.$tileColor, _properties.$tileSize]
};

exports.getProperties = function($$elmt) {

	// Layout nodes have simple static properties
	if ($$elmt.hasClass("s-m-layout")) {
		var props;
		Object.keys(_layoutProps).some(function(key) {
			var cls = "s-m-" + key;
			if ($$elmt.hasClass(cls)) {
				props = _layoutProps[key];
				return true;
			}
			return false;
		});
		return props;
	}
};