"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var Base = require('syracuse-tablet/html/js/application/daoSdata').Dao;
var sdataUtils = require('syracuse-tablet/html/js/sdata/sdataUtils');


/**
 * Manages local pagination
 */
var _Klass = utils.defineClass(function(arrayCtrl, options) {
	options = options || {};
	this._arrayCtrl = arrayCtrl;
	this._count = options.count || 10;
	this._firstIdx = options.start || 0;
	this._lastIdx = options.length - 1;
	this.first();

}, null, {
	destroy: function(destroyProto) {
		this._arrayCtrl = null;
	},
	first: function() {
		this._curIdx = this._firstIdx;
		return this._curIdx;
	},
	last: function() {
		this._curIdx = this._lastIdx;
		return this._curIdx;
	},
	next: function() {
		this._curIdx = Math.min(this._lastIdx, this._curIdx + 1);
		return this._curIdx;
	},
	prev: function() {
		this._curIdx = Math.max(this._firstIdx, this._curIdx - 1);
		return this._curIdx;
	},
});