"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var Base = require('syracuse-tablet/html/js/controls/array/arrayBase').Klass;
var builder = require('syracuse-tablet/html/js/controls/array/builderLookup').Klass;

/**
 * Array control for a LOOKUP FACET
 * TODO - Merge with arrayStd if nothing special except readArrayData
 */
var _Klass = utils.defineClass(
	function(controller, article, prototype) {
		var self = this;
		Base.call(self, controller, article, prototype);
		self.setBuilder(new builder(self));
	}, Base, {

		readArrayData: function(controllerDao) {
			return controllerDao.getLookupData(this.prototype);
		}

	});

exports.Klass = _Klass;