"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var Base = require('syracuse-tablet/html/js/controls/array/arrayBase').Klass;
var builderGrid = require('syracuse-tablet/html/js/controls/array/builderGrid').Klass;

/**
 * Array control for a DETAIL/EDIT FACET
 * TODO - Merge with arrayStd if nothing special except readArrayData
 */
var _Klass = utils.defineClass(
	function(controller, article, prototype, options) {
		var self = this;
		Base.call(self, controller, article, prototype, options);
		self.setBuilder(new builderGrid(self, options));
	}, Base, {

		readArrayData: function(controllerDao) {
			return controllerDao.getArrayData(this.$bind, this.prototype);
		}
	});

exports.Klass = _Klass;