"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');

/**
 * BASE builder for LAYOUTS and CONTROLS
 * 	Just provide a destroy and access to parent control/layout article and prototype...
 *  Temporary to split control classes from build ui classes in order to avoid conflicts
 */
var _BuilderBase = utils.defineClass(
	function(ctrl) {
		var self = this;
		self.ctrl = ctrl;
		self.id = ctrl.id;
		self.article = ctrl.article;
		self.prototype = ctrl.prototype;
		self.displayOptions = ctrl.displayOptions;
	}, null, {
		destroy: function() {
			this.ctrl = null;
			this.article = null;
			this.prototype = null;
		},
		buildHtml: function(html, controllerDao, buildOptions) {
			throw new Error("BuildHtml Not implemented");
		}
	});

exports.BuilderBase = _BuilderBase;