"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var log = require('syracuse-tablet/html/js/helpers/logger').getLogger("ctrl");


var _Control = utils.defineClass(
	function(controller, type, options) {
		var self = this;
		self.controller = controller;
		self.type = type;
		self.options = options || {};
		self.$elmt = null;
	}, null, {
		destroy: function() {
			var self = this;
			self.controller = null;
			utils.unbindObj(self.$elmt);
			utils.unbindObj(self);
		}
	});

exports.Control = _Control;